tooltipClass=".tooltip";function str_replace(c,b,a){return a.split(c).join(b)}this.tooltip=function(){xOffset=10;yOffset=20;fadeInTime=400;jQuery(tooltipClass).hover(function(a){this.t=this.title;this.title="";this.t=str_replace("::","<br />",this.t);this.t=str_replace("[!]","<span class='tooltipTitle'>",this.t);this.t=str_replace("[/!]","</span><br />",this.t);this.t=str_replace("[","<",this.t);this.t=str_replace("]",">",this.t);if(this.t!=""){jQuery("body").append("<p id='tooltip'>"+this.t+"</p>");jQuery("#tooltip").css("top",(a.pageY-xOffset)+"px").css("left",(a.pageX+yOffset)+"px").fadeIn(fadeInTime)}},function(){this.title=this.t;jQuery("#tooltip").remove()});jQuery(tooltipClass).mousemove(function(a){jQuery("#tooltip").css("top",(a.pageY-xOffset)+"px").css("left",(a.pageX+yOffset)+"px")})};jQuery(document).ready(function(){tooltip()});